import {Injectable} from '@angular/core';
import {IChatConfig} from "../../interfaces/chat-config.interface";
import {BehaviorSubject} from "rxjs";

@Injectable({
  providedIn: 'root'
})
export class ChatState {
  private _config: BehaviorSubject<IChatConfig | null> = new BehaviorSubject<IChatConfig | null>(null);

  public get config(): IChatConfig | null {
    return this._config.value;
  }

  public set setConfig(config: IChatConfig) {
      this._config.next(config);
  }
}
