import {Component, Input, OnChanges, OnInit, SimpleChanges} from '@angular/core';
import {NgClass} from "@angular/common";
import {ChatState} from "../../services/chat-state/chat.state";
import {IConfig} from "../../app.component";

@Component({
  selector: 'app-start-chat-form',
  templateUrl: './start-chat-form.component.html',
  styleUrls: ['./start-chat-form.component.scss'],
  standalone: true,
  imports: [NgClass]
})
export class StartChatFormComponent implements OnChanges {
  @Input() config: IConfig = {
    color: 'red'
  };

  public isCollapsed: boolean = false;

  constructor(
    public chatState: ChatState
  ) {
  }

  public ngOnChanges(changes: SimpleChanges): void {
    const el: HTMLElement | null = document.querySelector('.chat__start-btn');
    console.log(changes);
    if (el) {
        el.style.backgroundColor = changes['config'].currentValue.color;
    }
  }

  public collapse() {
    this.isCollapsed = !this.isCollapsed;
  }
}
