import {Component, Injector, Input, OnInit} from '@angular/core';
import {StartChatFormComponent} from "./components/start-chat-form/start-chat-form.component";
import {ChatState} from "./services/chat-state/chat.state";
import {createCustomElement} from "@angular/elements";

export interface IConfig {
  color: string;
}

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.scss'],
  standalone: true,
  imports: [StartChatFormComponent]
})
export class AppComponent implements OnInit {
  @Input() public apiKey: string = '';

  @Input() public config: IConfig = {
    color: 'red'
  };

  constructor(
    private injector: Injector,
    private _chatState: ChatState
  ) {
  }

  public ngOnInit(): void {
    window.addEventListener('message', this.receiveMessage.bind(this));
  }

  receiveMessage(event: MessageEvent): void {
    // Проверьте, что сообщение пришло с вашего домена
    console.log(event)
    if (event.origin !== 'http://localhost:4200') {
      return;
    }

    this._chatState.setParams(event.data.config);
  }
}
