import {Component, OnInit} from '@angular/core';
import {NgClass} from "@angular/common";
import {ChatState} from "../../services/chat-state/chat.state";

@Component({
  selector: 'app-start-chat-form',
  templateUrl: './start-chat-form.component.html',
  styleUrls: ['./start-chat-form.component.scss'],
  standalone: true,
  imports: [NgClass]
})
export class StartChatFormComponent implements OnInit {
  public isCollapsed: boolean = false;

  constructor(
    public chatState: ChatState
  ) {
  }

  public ngOnInit(): void {
    const el: HTMLElement | null = document.querySelector('.chat__start-btn');
    if (el) {
      el.style.backgroundColor = this.chatState.config.color;
    }
  }

  public collapse() {
    this.isCollapsed = !this.isCollapsed;
  }
}
<div class="chat" [ngClass]="{'chat--collapsed': isCollapsed}">
  <div class="chat__icon chat-icon">
    <h3 class="chat-icon__title">Chat</h3>
    <button class="chat-icon__collapse" (click)="collapse()">&times;</button>
  </div>
  <div class="chat__content">
    <div class="chat__body">
      <div class="chat__form">
        <input type="text" placeholder="Name" />
        <input type="email" placeholder="Email" />
        <textarea placeholder="Question"></textarea>
      </div>
      <button class="chat__start-btn">Start chat</button>
    </div>
  </div>
</div>
